import React from "react";
import { Button, CircularProgress, Typography } from "@mui/material";
import { Box } from "@mui/system";

import { decode } from "html-entities";

import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import useAxios from "../hooks/Axios";

import { handleScoreChnage } from "../Redux/action";

const getRandomInt = (max) => {
  return Math.floor(Math.random() * Math.floor(max));
};

const Questions = () => {
  const {
    question_category,
    question_difficulty,
    question_type,
    amount_of_question,
    score,
    name,
    question_answers
  } = useSelector((state) => state);

  const history = useHistory();
  const dispatch = useDispatch();

  let apiUrl = `/api.php?amount=${amount_of_question}`;

  // if (question_category) {
  //   apiUrl = apiUrl.concat(`&category=${question_category}`);
  // }

  // if (question_difficulty) {
  //   apiUrl = apiUrl.concat(`&difficulty=${question_difficulty}`);
  // }

  // if (question_type) {
  //   apiUrl = apiUrl.concat(`&type=${question_type}`);
  // }
  useEffect(() => {
    console.log("inital : ", question_answers)
  }, [])

  const { response, loading } = useAxios({ url: apiUrl });
  const [questionIndex, setQuestionIndex] = useState(0);
  const [options, setOptions] = useState([]);
  const [selectedIndex, setSelectedIndex] = React.useState(1);
  const [enableNext,setEnableNext] = useState(false)
  const [intialRender,setInitialRender] = useState(true)

  const handleListItemClick = (
    number
  ) => {
    setSelectedIndex(number);
  };
  useEffect(() => {
    if (question_answers?.length) {
      const question = question_answers[questionIndex];
      console.log("question ", question.answers)
      let answers = []
      for (const [key, value] of Object.entries(question.answers)) {
        console.log("values", value);

        if (value)
          answers.push({
            [key]: value
          })
      }
      answers.splice(
        getRandomInt(question.answers.length),
        0,
        // question.correct_answer
      );
      setOptions(answers);
    }
  }, [question_answers, questionIndex]);

  if (loading) {
    return (
      <Box mt={20}>
        <CircularProgress />
      </Box>
    );
  }

  const handleClickAnswer = (name,next) => {
    const question = question_answers[questionIndex];
    if (name === question.correct_answer) {
      dispatch(handleScoreChnage(score + 1));
    }

    if (next && questionIndex + 1 < question_answers.length) {
      setQuestionIndex(questionIndex + 1);
      setSelectedIndex(0)
    } else if(next){
      history.push("/score");
    }
  };

  return (
    <>
    <Box display={"flex"} justifyContent={"space-between"}>
    <Typography variant="h4">Welcome {name}</Typography>
    <Typography variant="h4">Questions {questionIndex + 1 + "/" + question_answers.length}</Typography>
    </Box>
    <Card  sx={{ minWidth: 245}}>
      <CardContent>
          <Typography mt={5}>
            {/* {decode(response.results[questionIndex].question)} */}
            {question_answers[questionIndex].question}
          </Typography>
          {options.map((data, id) => {
            if(intialRender){
              s
            }
            <Box  mt={2} key={id}>
              <List  component="nav" aria-label="main mailbox folders">
                <ListItemButton sx={{border:"1px solid black"}} name={Object.keys(data)}
                  selected={selectedIndex ? id === selectedIndex : null}
                  onClick={() =>{ handleListItemClick(id);
                     handleClickAnswer(Object.keys(data)) 
                  }}
                >
                  <ListItemText primary={Object.values(data)} />
                </ListItemButton>
              </List>
              {/* <Button name={Object.keys(data)} onClick={handleClickAnswer} variant="contained">
            {Object.values(data)}
          </Button> */}
            </Box>
})}
          <Box mt={5}>
            Score: {score} / {question_answers.length}
          </Box>
          <CardActions >
            <Box display={"flex"} width={"100%"} justifyContent={"space-between"}>
          {/* <Button variant="contained" size="medium">PREV</Button> */}
        <Button onClick={(e)=>{handleClickAnswer(null,true)}} variant="contained" size="medium">
          {questionIndex+1 === question_answers.length ? "SUBMIT" : "NEXT"}
        </Button>
        </Box>
      </CardActions>
      </CardContent>
    </Card>
    </>
  );
};

export default Questions;
