import React,{useState} from "react";
import { useHistory } from "react-router-dom";

import { Button, CircularProgress, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { Formik } from "formik";
import SelectField from "../Components/SelectField";
import TextFieldCom from "../Components/TextField";
import { useDispatch, useSelector } from "react-redux";

import useFetchData from "../hooks/Axios";

const Setting = () => {
  const { response, loading, error } = useFetchData({ url: "/api_category.php" });
const [name,setName] = useState("")
  const history = useHistory();
  const { question_category,question_difficulty,question_limit } = useSelector((state) => state);


  if (loading) {
    return (
      <Box mt={20}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Typography variant="h6" mt={20} color="red">
        Something went wrong!!
      </Typography>
    );
  }

  const categoryOptions = [
    { id: "Random", name: "Random" },
    { id: "Linux", name: "Linux" },
    { id: "DevOps", name: "DevOps" },
    { id: "Networking", name: "Networking" },
    { id: "Programming (PHP, JS, Pythong and etc.)", name: "Programming (PHP, JS, Pythong and etc.)" },
    { id: "Cloud", name: "Cloud" },
    { id: "Docker", name: "Docker" },
  ]



  const difficultyOption = [
    { id: "easy", name: "Easy" },
    { id: "medium", name: "Medium" },
    { id: "hard", name: "Hard" },
  ];

  const typeOptions = [
    { id: "5", name: "5" },
    { id: "10", name: "10" },
    { id: "15", name: "15" },
    { id: "20", name: "20" },
  ];

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("event : ", question_category);
    const url = `https://quizapi.io/api/v1/questions?${question_category ? "category="+question_category : ""}${question_difficulty ? "&difficulty="+question_difficulty : ""}${question_limit ? "&limit="+question_limit : ""}`
    console.log("event : ", url);
    // history.push("/question");
  };


  return (

        <form onSubmit={handleSubmit}>
          <Box mt={3} width="100%">
            <TextField
             onChange={(e)=>{
              setName(e.target.value)
             }}

              sx={{ width: "100%" }}
              required
              id="standard"
              label="Name"
            />
          </Box>
          <SelectField options={categoryOptions} label="Category" />
          <SelectField options={difficultyOption} label="Difficulty" />
          <SelectField options={typeOptions} label="Limit" />

          <Box mt={3} width="100%">
            <Button fullWidth variant="contained" type="submit">
              Get Started
            </Button>
          </Box>
        </form>

  );
};

export default Setting;
