import React from "react";
import { Button, CircularProgress, Typography } from "@mui/material";
import { Box } from "@mui/system";

import { decode } from "html-entities";

import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import useAxios from "../hooks/Axios";
import {Container} from "@mui/material";
import { handleScoreChnage,userAnswers } from "../Redux/action";
import { array } from "yup";

const getRandomInt = (max) => {
  return Math.floor(Math.random() * Math.floor(max));
};

const answerResponse = {
    "answer_a":"Option A",
    "answer_b":"Option B",
    "answer_c":"Option C",
    "answer_d":"Option D",
    "answer_e":"Option E",
    "answer_f":"Option F",
}

const ViewQuestion = () => {
  const {
    question_category,
    question_difficulty,
    question_type,
    amount_of_question,
    score,
    name,
    question_answers
  } = useSelector((state) => state);

  const history = useHistory();
  const dispatch = useDispatch();

  let apiUrl = `/api.php?amount=${amount_of_question}`;

  // if (question_category) {
  //   apiUrl = apiUrl.concat(`&category=${question_category}`);
  // }

  // if (question_difficulty) {
  //   apiUrl = apiUrl.concat(`&difficulty=${question_difficulty}`);
  // }

  // if (question_type) {
  //   apiUrl = apiUrl.concat(`&type=${question_type}`);
  // }


  const { response, loading } = useAxios({ url: apiUrl });
  const[userData,setUserData] = useState({})
  const [questionIndex, setQuestionIndex] = useState(0);
  const [options, setOptions] = useState([]);
  const [selectedIndex, setSelectedIndex] = React.useState(1);
  const [enableNext, setEnableNext] = useState(false)
  const [intialRender, setInitialRender] = useState(false)

  useEffect(() => {
    setUserData(history.location.state.userData)
    console.log("looo",userData);
  }, [])

  const handleListItemClick = (
    number
  ) => {
    setSelectedIndex(number);
  };
  useEffect(() => {
    if (userData.question_answers?.length) {
      const question = userData.question_answers[questionIndex];
      console.log("question ", userData.question)
      let answers = []
      for (const [key, value] of Object.entries(question.answers)) {
        console.log("values", value);

        if (value)
          answers.push({
            [key]: value
          })
      }
      answers.splice(
        getRandomInt(question.answers.length),
        0,
        // question.correct_answer
      );
      setOptions(answers);
    }
  }, [userData.question_answers, questionIndex]);

  if (loading) {
    return (
      <Box mt={20}>
        <CircularProgress />
      </Box>
    );
  }

  const handleClickAnswer = (name, next) => {
    const question = question_answers[questionIndex];
    if (next && questionIndex + 1 < question_answers.length) {
      setQuestionIndex(questionIndex + 1);
    }
  };

  return (
    <><Container maxWidth={"xl"} >
    <Box mt={2} mb={2} display={"flex"} justifyContent={"space-between"}>
        <Typography variant="h4">Welcome {userData.name}</Typography>
        <Typography variant="h4">{userData.question_category}</Typography>
        <Typography variant="h4">Questions {questionIndex + 1 + "/" + userData.question_answers.length}</Typography>
      </Box>
      </Container>
      <Container  maxWidth={"md"}>
      <Card sx={{ minWidth: 245 }}>
        <CardContent>
          <Typography mt={5}>

            {console.log("ayooooo",userData.question_answers)}
            {userData.question_answers[questionIndex].question}
          </Typography>
          {options.map((data, id) => (
            <Box mt={2} key={id}>
              <List component="nav" aria-label="main mailbox folders">
                <ListItemButton disabled sx={{ border: "1px solid black" }} name={Object.keys(data)}
                  selected={Object.keys(data) === userData.user_answers[selectedIndex-1]}
                  onClick={() => {
                    setInitialRender(false);
                    handleListItemClick(id);
                  }}
                >
                  <ListItemText primary={Object.values(data)} />
                </ListItemButton>
              </List>

            </Box>
          ))}

          <CardActions >
            <Box display={"flex"} width={"100%"} justifyContent={"space-between"}>
              {/* <Button variant="contained" size="medium">PREV</Button> */}
             {userData.question_answers.length == questionIndex+1 ?   <Button variant="contained" onClick={() => {history.push("/admin")}}>Back to Dashboard</Button> :
              <Button variant="contained" onClick={() => {setQuestionIndex(questionIndex+1)}}>NEXT</Button>
             }
              <Typography  variant="h5" sx={{fontWeight:"bolder"}}>User Answer : {answerResponse[userData.user_answers[selectedIndex-1]]}</Typography>
            </Box>

          </CardActions>
        </CardContent>
      </Card>
      </Container>
    </>
  );
};

export default ViewQuestion;
