import {
  CHANGE_CATEGORY,
  CHANGE_DIFFICULTY,
  CHANGE_AMOUNT,
  CHANGE_LIMIT,
  CHANGE_SCORE,
} from "./actionTypes";

export const handleCategoryChange = (payload) => ({
  type: CHANGE_CATEGORY,
  payload,
});
export const handleDifficultyChange = (payload) => ({
  type: CHANGE_DIFFICULTY,
  payload,
});
export const handleAmountC = (payload) => ({
  type: CHANGE_AMOUNT,
  payload,
});
export const handleScoreChnage = (payload) => ({
  type: CHANGE_SCORE,
  payload,
});
export const handleLimitChnage = (payload) => ({
  type: CHANGE_LIMIT,
  payload,
});
