import React,{useState} from "react";
import { useHistory } from "react-router-dom";

import { Button, CircularProgress, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { Formik } from "formik";
import SelectField from "../Components/SelectField";
import TextFieldCom from "../Components/TextField";

import Axios from "../hooks/Axios";

const Setting = () => {
  const { response, loading, error } = Axios({ url: "/api_category.php" });

  const history = useHistory();

    const [formData, setFormData] = useState({
      category: '',
      difficulty: '',
      limit: '',
    });

    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    };

    if (loading) {
      return (
        <Box mt={20}>
          <CircularProgress />
        </Box>
      );
    }

    if (error) {
      return (

        <Typography variant="h6" mt={20} color="red">
          Something went wrong!!
        </Typography>
      );
    }

    const categoryOptions = [
      { id: "Linux", name: "Linux" },
      { id: "DevOps", name: "DevOps" },
      { id: "Networking", name: "Networking" },
      { id: "Programming (PHP, JS, Pythong and etc.)", name: "Programming (PHP, JS, Pythong and etc.)" },
      { id: "Cloud", name: "Cloud" },
      { id: "Docker", name: "Docker" },
    ]



    const difficultyOption = [
      { id: "easy", name: "Easy" },
      { id: "medium", name: "Medium" },
      { id: "hard", name: "Hard" },
    ];

    const typeOptions = [
      { id: "5", name: "5" },
      { id: "10", name: "10" },
      { id: "15", name: "15" },
      { id: "20", name: "20" },
    ];

    const handleSubmit = (event) => {
      event.preventDefault();
      console.log("event : ", event);
      // history.push("/question");
    };

    return (
 <>
   {console.log("render")}
          <form onSubmit={handleSubmit}>
            <Box mt={3} width="100%">
              <TextField
                // onChange={handleChange}
                sx={{ width: "100%" }}
                required
                id="standard"
                label="Name"
              />
            </Box>
            <SelectField name="category" onChange={handleChange} options={categoryOptions} label="category" />
            <SelectField name="difficulty" onChange={handleChange} options={difficultyOption} label="Difficulty" />
            <SelectField name="limit" onChange={handleChange} options={typeOptions} label="Type" />

            <Box mt={3} width="100%">
              <Button fullWidth variant="contained" type="submit">
                Get Started
              </Button>
            </Box>
          </form>
          </>
    );
  };

  export default Setting;
