import { Button, Container, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";
import { LinearProgress } from '@mui/material';
import { handleLimitChange, handleScoreChnage, userAnswers } from "../Redux/action";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import { useEffect } from "react";



const Final = () => {
  const disptach = useDispatch();
  const history = useHistory();

  const { question_difficulty, name, question_limit, question_answers, question_category, score, user_answers, } = useSelector((state) => state);
  let percentageValue = ((score / question_answers.length) * 100).toFixed(0);
  useEffect(() => {

    window.history.forward();
    function noBack() {
      window.history.forward();
    }
    noBack()
    const userResult = {
      name: name,
      question_difficulty: question_difficulty,
      question_limit: question_limit,
      question_category,
      score: score,
      user_answers: user_answers,
      percentageValue: percentageValue,
      question_answers: question_answers
    }

    let getUserDetails = localStorage.getItem("userDetails")

    if (!getUserDetails) {
      var userArray = []
      localStorage.setItem("userDetails", JSON.stringify(userArray))
    } else {
      console.log("data", localStorage.getItem("userDetails"))
      if (localStorage.getItem("userDetails")) {
        localStorage.setItem("userDetails", JSON.stringify([...JSON.parse(localStorage.getItem("userDetails")), userResult]))
      }
    }

    //  let addUser = getUserDetails.push(userResult)
    //  localStorage.setItem("userDetails",addUser)
  }, [])

  const handleBackToSettings = () => {
    disptach(handleScoreChnage(0));
    disptach(handleLimitChange(0));
    history.push("/");
  };


  return (
    <Box display={"lex"} justifyContent={"center"} alignItems={"center"} height={"100vh"}>
      <Container maxWidth={"sm"} sx={{ boxShadow: 5 }}>
        {console.log("final answers : ", user_answers)}
        <Card sx={{ minWidth: 275 }}>
          <CardContent>
            <Typography variant="h4" fontWeight="bold" mb={2}>
              Congratulations !
            </Typography>
            <Typography variant="h" fontWeight="bold" mb={2}>
              Final Score : {score}/{question_answers.length}
            </Typography>
            <Typography variant="h5" fontWeight="bold" mb={2}>
              Your Percentage : {percentageValue + "%"}
            </Typography>
          </CardContent>
          <CardActions sx={{ justifyContent: "center" }}>
            <Button onClick={handleBackToSettings} variant="outlined">
              back to settings!
            </Button>
          </CardActions>
        </Card>
      </Container>
    </Box>
  );
};

export default Final;
