import React from "react";
import { useHistory } from "react-router-dom";
import { Formik } from "formik";
import * as Yup from 'yup';
import { Button, CircularProgress, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";
import SelectField from "../Components/SelectField";
import TextFieldCom from "../Components/TextField";

import Axios from "../hooks/Axios";

const Setting = () => {
  const { response, loading, error } = Axios({ url: "/api_category.php" });

  const history = useHistory();

  const validationSchema = Yup.object().shape({
    name: Yup.string().required('Name is required'),
    email: Yup.string().email('Invalid email').required('Email is required'),
    message: Yup.string().required('Message is required'),
  });


    const handleSubmit = (values) => {
      // Handle the form submission here
      console.log(values); // You can access the form data here and send it to the server or do any processing.
    };


  if (loading) {
    return (
      <Box mt={20}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Typography variant="h6" mt={20} color="red">
        Something went wrong!!
      </Typography>
    );
  }

  const categoryOptions = [
    { id: "Linux", name: "Linux" },
    { id: "DevOps", name: "DevOps" },
    { id: "Networking", name: "Networking" },
    { id: "Programming (PHP, JS, Pythong and etc.)", name: "Programming (PHP, JS, Pythong and etc.)" },
    { id: "Cloud", name: "Cloud" },
    { id: "Docker", name: "Docker" },
  ]



  const difficultyOption = [
    { id: "easy", name: "Easy" },
    { id: "medium", name: "Medium" },
    { id: "hard", name: "Hard" },
  ];

  const typeOptions = [
    { id: "5", name: "5" },
    { id: "10", name: "10" },
    { id: "15", name: "15" },
    { id: "20", name: "20" },
  ];

  // const handleSubmit = (event) => {
  //   event.preventDefault();
  //   console.log("event : ", event);
  //   // history.push("/question");
  // };

  return (
    <Formik
      initialValues={{ name: '', email: '', message: '' }}
      validationSchema={validationSchema}
      onSubmit={handleSubmit}
    >
      {({ touched, errors }) => (
        <form onSubmit={handleSubmit}>
          <Box mt={3} width="100%">
            <TextField
            onChange={handleChange}
              sx={{ width: "100%" }}
              required
              id="standard"
              label="Name"
            />
          </Box>
          <SelectField options={categoryOptions} label="category" />
          <SelectField options={difficultyOption} label="Difficulty" />
          <SelectField options={typeOptions} label="Type" />

          <Box mt={3} width="100%">
            <Button fullWidth variant="contained" type="submit">
              Get Started
            </Button>
          </Box>
        </form>
      )}
    </Formik>
  );
};

export default Setting;
